import React, { Component }  from 'react';
import {View, Text, TextInput, StyleSheet, TouchableOpacity, Image} from 'react-native';

//Create the Login Page
const LoginScreen =() =>{
    return ( 
        <View style={styles.container}>  
            <Image style={styles.backgroundImage} source={require('../images/TraceBio-White.png')}></Image>    
            <TextInput placeholder='Username' style={styles.inputFields}></TextInput> 
            <TextInput placeholder='Password' style={styles.inputFields}></TextInput>        
            <TouchableOpacity style={styles.button}>
                <Text style={styles.buttonText} >LOGIN</Text>
            </TouchableOpacity>
        </View>
    )
};

//All styling options created below
const styles= StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor:'#b7b7b7',
        justifyContent: 'center'
      },
      backgroundImage:{
        //flex: 1,
        //resizeMode: "cover",
        //justifyContent: "center",                
      },
    inputFields:{
        borderBottomWidth:1,
        backgroundColor: '#FFFFFF',
        marginHorizontal: '10%',
        marginVertical: 10,
        padding:10,
        fontWeight: 'bold'
    },
    button:{
        //alignSelf: 'center',
        //width: '60%',
        alignItems: 'center',
        marginHorizontal: '10%',
        marginVertical: 10,
        padding:10,
        borderRadius:20,
        backgroundColor:'#ff0000',            
    },
    buttonText:{
        color: '#FFFFFF',
        fontWeight: 'bold'
    }
});

export default LoginScreen;
