import React from 'react';
import {View, Text, StyleSheet, Button, ImageBackground, TouchableOpacity} from 'react-native';

const image = '../images/Trace-3D.png';
//Create the Welcome Screen component
const WelcomeScreen =() =>{
    return ( 
        <View >
            <ImageBackground style={styles.backgroundImage} source={require('../images/Trace-3D.png')}>
                <TouchableOpacity  color="#841584" style={styles.button}>
                    <Text>Login</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.button}>
                    <Text>Sign Up</Text>
                </TouchableOpacity>
            </ImageBackground> 

        </View>
    )
};

//All styling options created below
const styles= StyleSheet.create({
    container: {
        flex: 1,
      
        //justifyContent: 'center'
    //     flex: 1,
    // position: 'relative',
    // resizeMode: 'cover'
        
      },
    backgroundImage:{
        flex: 1,
        resizeMode: "cover",
        justifyContent: "center",        
      },
    button:{
        //flex: 1,
        //position: "absolute",
        //marginTop: 2,
        //marginLeft:50,
        alignItems: "center",
        alignContent: "center",
        //justifyContent: 'center',  
        //padding:5,
        maxWidth:250,
        backgroundColor:'#FFFFFF'
        
    }
});

export default WelcomeScreen;
