import React from 'react';
import {View, Text, StyleSheet, Button, ImageBackground, TouchableOpacity} from 'react-native';

const image = '../images/Trace-3D.png';
//Create the Welcome Screen component
const WelcomeScreen =() =>{
    return ( 
        <View style={styles.container}>
            <ImageBackground style={styles.backgroundImage} source={require('../images/Trace-3D.png')}>
                <TouchableOpacity style={styles.button}>
                    <Text style={styles.buttonText}>Login</Text>
                </TouchableOpacity>
                <TouchableOpacity style={styles.button}>
                    <Text style={styles.buttonText}>Sign Up</Text>
                </TouchableOpacity>
            </ImageBackground> 

        </View>
    )
};

//All styling options created below
const styles= StyleSheet.create({
    container: {
        flex: 1,
      
        //justifyContent: 'center'
    //     flex: 1,
    // position: 'relative',
    // resizeMode: 'cover'
        
      },
    backgroundImage:{
        flex: 1,
        resizeMode: "cover",
        justifyContent: "center",   
        blurRadius:1    
      },
    button:{
        //flex: 1,
        //position: "absolute",
        // //alignContent:'stretch',
        // alignItems: 'center',
        // flexDirection: 'row',
        alignSelf: 'center',
        width: '70%',
        alignItems: 'center',
        margin: 10,
        padding:10,
        backgroundColor:'#ff0000',            
    },
    buttonText:{
        color: '#FFFFFF'
    }
});

export default WelcomeScreen;
