import React from 'react';
import {View, Text, StyleSheet, Button, ImageBackground} from 'react-native';

const image = '../images/Trace-3D.png';
//Create the Welcome Screen component
const WelcomeScreen =() =>{
    return ( 
        <View style={styles.container}>
            {/* <ImageBackground style={styles.backgroundImage} source={require('../images/Trace-3D.png')}> */}
                <Button  color="#841584" style={styles.button} title='Log In'></Button>
                <Button style={styles.button} title='Sign Up'></Button> 
            {/* </ImageBackground> */}

        </View>
    )
};

//All styling options created below
const styles= StyleSheet.create({
    container: {
        flex: 1,
      
        justifyContent: 'center'
    //     flex: 1,
    // position: 'relative',
    // resizeMode: 'cover'
        
      },
    backgroundImage:{
        flex: 1,
        resizeMode: "cover",
        justifyContent: "center",
         
      },
    button:{
        //flex: 1,
        //position: "absolute",
        alignItems: "center",
        justifyContent: 'center',
        backgroundColor:'#FFFFFF'
        
    }
});

export default WelcomeScreen;
